time for i in `ls`; do echo $i; perl -e '<>; $name=<>; chop($name); $_=<>; die "bad" unless m/Inherit/; while(<>) {if(m/^[>]/) {print (" ", $name, ":", join(",",@refs), ";", join(",",@syms), "\n"); exit} elsif(m/^[ABDLTS].* +(.*)/) {push(@refs, $1) } else {m/^.* (.*)/ or die; push(@syms, $1)}}' $i >>00; done
